    /****************************************************
     * CLIQUE NO BOTAO ENTRAR
    ****************************************************/
    private void jbEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbEntrarActionPerformed
        try {
            UsuarioData daoDataUser = new UsuarioData();
            Usuario objUser = daoDataUser.login(jtfEmail.getText(), jtpSenha.getText());
            
            if(objUser != null) {
                new JFPrincipal(objUser).setVisible(true);
                dispose();
                return;
            }
            
            // EXIBINDO MENSAGEM DE LOGIN INVALIDO COM INTERVALO DE EXIBICAO
            jlLoginInvalido.setVisible(true);
            UtilsFrontEnd.setTimeout(() -> {
                jlLoginInvalido.setVisible(false);
            }, 2500);
        } catch (Exception ex) { 
            System.err.println("ERROR: "+ex); // MENSAGEM DO ERRO NO CONSOLE
            JOptionPane.showMessageDialog(null, "Ocorreu algum erro ao buscar o usuário. Verifique seus dados e tente novamente!", 
                                                "Erro ao fazer login", JOptionPane.ERROR_MESSAGE); // MENSAGEM EXIBIDA AO USUARIO
        }
    }//GEN-LAST:event_jbEntrarActionPerformed

