    /****************************************************
     * CLIQUE NO CHECKBOX DE VER SENHA
     ****************************************************/
    private void checkboxVerSenhaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_checkboxVerSenhaMouseClicked
        // VERIFICANDO SE O CHECKBOX ESTÁ CHECKADO
        if(!checkboxVerSenha.isSelected()) {
            jlSenhaDigitada.setVisible(false);
            return;
        }
        
        jlSenhaDigitada.setText(jtpSenha.getText());
        jlSenhaDigitada.setVisible(true);
    }//GEN-LAST:event_checkboxVerSenhaMouseClicked

    /****************************************************
     * TECLA O TECLADO
     ****************************************************/
    private void jtpSenhaKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jtpSenhaKeyReleased
        jlSenhaDigitada.setText(jtpSenha.getText());
    }//GEN-LAST:event_jtpSenhaKeyReleased

